/* Post /api/posts
{
    title: '제목',
    body: '내용',
    tags: ['태그1', '태그2']
} */
       const write = async ctx => {
    const {title, body, tags} = ctx.request.body; 
    const post = new Post({
        title,
        body, 
        tags,
    });
    try {
        await post.save();
        ctx.body = post;
    } catch (e) {
        ctx.throw(500, e);
    }
};

